<script>
// Enhanced game engine with smooth animations and professional features
class TermTargeterGame {
  constructor() {
    // Game data from the "Insect Genetics" article
    this.roundsData = [
{ term: 'Tripartite synapse', clue: 'Concept of synaptic communication involving two neurons plus an astrocyte' },
{ term: 'Excitotoxicity', clue: 'Neuron death triggered by excessive glutamate or overexcitation' },
{ term: 'Blood-brain barrier', clue: 'Selective CNS border whose tightness astrocyte endfeet help reinforce' },
{ term: 'Lactate', clue: 'Energy source preferred by neurons during high metabolic demands' },
{ term: 'Aquaporin channels', clue: 'Water pores astrocytes use during sleep to circulate fluid and clear waste' }
];

    this.distractorsData = [
'Saltatory conduction',
'Node of Ranvier',
'Myelin sheath',
'GABAergic interneuron',
'Synaptic vesicle recycling',
'Glucose'
];

    // DOM elements
    this.elements = {
      area: document.getElementById('game-area'),
      clue: document.getElementById('clue'),
      scoreValue: document.getElementById('score-value'),
      accuracyValue: document.getElementById('accuracy-value'),
      triesValue: document.getElementById('tries-value'),
      message: document.getElementById('game-end-message-area'),
      startBtn: document.getElementById('start-btn'),
      stopBtn: document.getElementById('stop-btn'),
      resetBtn: document.getElementById('reset-btn'),
      stars: document.querySelector('.stars')
    };

    // Game state
    this.state = {
      currentRoundIndex: 0,
      score: 0,
      totalAttempts: 0,
      triesLeft: 2,
      maxConcurrentTargets: 4,
      lanesBusy: [false, false, false, false],
      currentRoundTargetPool: [],
      activeTargets: new Set(),
      gameStopped: true,
      spawnInterval: null,
      animationFrame: null,
      difficulty: 1,
      combo: 0
    };

    // Performance optimization
    this.targetPool = [];
    this.particlePool = [];

    // Initialize
    this.init();
  }

  init() {
    // Set random theme color
    this.setRandomTheme();

    // Create starfield
    this.createStarfield();

    // Bind events
    this.bindEvents();

    // Initialize game state
    this.resetGame();

    // Start animation loop
    this.startAnimationLoop();
  }

  setRandomTheme() {
    const themes = [
      { primary: '#d32f2f', secondary: '#ff8a65' }, // Red theme from old game
      { primary: '#007aff', secondary: '#4fc3f7' }, // Blue theme from old game
      { primary: '#af52de', secondary: '#9575cd' }, // Purple theme from old game
      { primary: '#3b82f6', secondary: '#f59e0b' },
      { primary: '#8b5cf6', secondary: '#10b981' },
      { primary: '#06b6d4', secondary: '#f43f5e' }
    ];

    const theme = themes[Math.floor(Math.random() * themes.length)];
    document.documentElement.style.setProperty('--primary', theme.primary);
    document.documentElement.style.setProperty('--secondary', theme.secondary);
  }

  createStarfield() {
    const starCount = 50;
    this.elements.stars.innerHTML = '';

    for (let i = 0; i < starCount; i++) {
      const star = document.createElement('div');
      star.className = 'star';
      star.style.left = `${Math.random() * 100}%`;
      star.style.top = `${Math.random() * 100}%`;
      star.style.animationDelay = `${Math.random() * 3}s`;
      star.style.animationDuration = `${3 + Math.random() * 2}s`;
      this.elements.stars.appendChild(star);
    }
  }

  bindEvents() {
    this.elements.startBtn.addEventListener('click', () => this.startGame());
    this.elements.stopBtn.addEventListener('click', () => this.stopGame());
    this.elements.resetBtn.addEventListener('click', () => this.resetGame());

    // Prevent text selection during gameplay
    this.elements.area.addEventListener('selectstart', e => e.preventDefault());
  }

  startAnimationLoop() {
    const animate = () => {
      this.update();
      this.state.animationFrame = requestAnimationFrame(animate);
    };
    animate();
  }

  update() {
    // Update active targets - MODIFICATION: Improved target removal logic
    this.state.activeTargets.forEach(target => {
      if (target.element && !target.hit && target.element.parentNode) {
        const rect = target.element.getBoundingClientRect();
        const areaRect = this.elements.area.getBoundingClientRect();

        // MODIFICATION: More reliable check for targets that have moved off screen
        // Check if target has completely left the game area (with buffer)
        if (rect.right < areaRect.left - 50) {
          this.removeTarget(target);
        }
      }
    });

    // Update particles
    this.updateParticles();
  }

  updateParticles() {
    this.particlePool = this.particlePool.filter(particle => {
      if (particle.life <= 0) {
        particle.element.remove();
        return false;
      }
      particle.life--;
      return true;
    });
  }

  shuffle(array) {
    const arr = [...array];
    for (let i = arr.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [arr[i], arr[j]] = [arr[j], arr[i]];
    }
    return arr;
  }

  updateHUD() {
    const accuracy = this.state.totalAttempts > 0 
      ? Math.round((this.state.score / this.state.totalAttempts) * 100) 
      : 0;

    this.elements.scoreValue.textContent = `${this.state.score}/${this.roundsData.length}`;
    this.elements.accuracyValue.textContent = `${accuracy}%`;
    this.elements.triesValue.textContent = this.state.triesLeft;

    // Animate score change
    this.elements.scoreValue.style.transform = 'scale(1.2)';
    setTimeout(() => {
      this.elements.scoreValue.style.transform = 'scale(1)';
    }, 200);
  }

  createTarget(term, lane) {
    const target = document.createElement('div');
    target.className = 'target';
    target.textContent = term;
    target.dataset.term = term;
    target.dataset.lane = lane;

    // Position target
    const laneHeight = this.elements.area.clientHeight / this.state.maxConcurrentTargets;
    const y = lane * laneHeight + (laneHeight - 50) / 2; // MODIFICATION: Adjusted for smaller target height
    target.style.top = `${y}px`;
    target.style.left = `${this.elements.area.clientWidth}px`;

    // Set animation duration based on difficulty
    const baseDuration = 8;
    const duration = baseDuration - (this.state.difficulty * 0.5);
    target.style.animationDuration = `${duration}s`;

    // Create target object
    const targetObj = {
      element: target,
      term: term,
      lane: lane,
      hit: false
    };

    // Add click handler
    target.addEventListener('click', (e) => this.handleTargetClick(e, targetObj));
    target.addEventListener('touchstart', (e) => this.handleTargetClick(e, targetObj), { passive: true });

    // MODIFICATION: Add animation end listener to ensure cleanup
    target.addEventListener('animationend', () => {
      if (!targetObj.hit) {
        this.removeTarget(targetObj);
      }
    });

    // Add to DOM and track
    this.elements.area.appendChild(target);
    this.state.activeTargets.add(targetObj);

    return targetObj;
  }

  handleTargetClick(event, target) {
    event.stopPropagation();

    if (target.hit || this.state.gameStopped) return;

    target.hit = true;
    this.state.totalAttempts++;

    const isCorrect = target.term === this.roundsData[this.state.currentRoundIndex].term;

    // Create click effect
    this.createClickEffect(event, isCorrect);

    if (isCorrect) {
      this.handleCorrectAnswer(target);
    } else {
      this.handleWrongAnswer(target);
    }
  }

  createClickEffect(event, isCorrect) {
    const rect = event.target.getBoundingClientRect();
    const x = rect.left + rect.width / 2;
    const y = rect.top + rect.height / 2;

    // Create particles
    for (let i = 0; i < 12; i++) {
      const particle = document.createElement('div');
      particle.className = 'particle';
      particle.style.left = `${x}px`;
      particle.style.top = `${y}px`;
      particle.style.background = isCorrect ? 'var(--success)' : 'var(--error)';

      const angle = (Math.PI * 2 * i) / 12;
      const velocity = 50 + Math.random() * 50;
      particle.style.setProperty('--tx', `${Math.cos(angle) * velocity}px`);
      particle.style.setProperty('--ty', `${Math.sin(angle) * velocity}px`);

      document.body.appendChild(particle);

      this.particlePool.push({
        element: particle,
        life: 60
      });

      setTimeout(() => particle.remove(), 1000);
    }
  }

  handleCorrectAnswer(target) {
    this.state.score++;
    this.state.combo++;

    // Animate target
    target.element.classList.add('correct');
    target.element.style.pointerEvents = 'none';

    // Update HUD
    this.updateHUD();

    // Show success feedback
    this.showFeedback('Correct! 🎯', 'success');

    // Play success sound (if implemented)
    this.playSound('success');

    // End round after animation
    setTimeout(() => {
      this.removeTarget(target);
      this.endRound(true);
    }, 600);
  }

  handleWrongAnswer(target) {
    this.state.triesLeft--;
    this.state.combo = 0;

    // Animate target
    target.element.classList.add('wrong');
    target.element.style.pointerEvents = 'none';

    // Update HUD
    this.updateHUD();

    // Show error feedback
    this.showFeedback('Wrong! Try again', 'error');

    // Play error sound (if implemented)
    this.playSound('error');

    // Remove target after animation
    setTimeout(() => {
      this.removeTarget(target);

      if (this.state.triesLeft <= 0) {
        this.finishGame();
      }
    }, 600);
  }

  showFeedback(message, type) {
    const feedback = document.createElement('div');
    feedback.style.cssText = `
      position: fixed;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      padding: 1rem 2rem;
      background: ${type === 'success' ? 'var(--success)' : 'var(--error)'};
      color: white;
      border-radius: 8px;
      font-weight: 600;
      z-index: 1000;
      animation: feedbackPop 0.5s ease-out;
    `;
    feedback.textContent = message;

    document.body.appendChild(feedback);

    setTimeout(() => feedback.remove(), 1000);
  }

  playSound(type) {
    // Placeholder for sound effects
    // In a real implementation, you would play audio files here
  }

  removeTarget(target) {
    if (target.element && target.element.parentNode) {
      target.element.remove();
    }
    this.state.lanesBusy[target.lane] = false;
    this.state.activeTargets.delete(target);
  }

  spawnTarget() {
    if (this.state.gameStopped || 
        this.state.activeTargets.size >= this.state.maxConcurrentTargets ||
        this.state.currentRoundTargetPool.length === 0) {
      return; }

    // Find free lane
    const freeLane = this.state.lanesBusy.findIndex(busy => !busy);
    if (freeLane === -1) return;

    // Get available terms
    const onScreen = Array.from(this.state.activeTargets).map(t => t.term);
    const available = this.state.currentRoundTargetPool.filter(t => !onScreen.includes(t));

    if (available.length === 0) return;

    // Mark lane as busy
    this.state.lanesBusy[freeLane] = true;

    // Select random term
    const term = available[Math.floor(Math.random() * available.length)];

    // Create target
    this.createTarget(term, freeLane);
  }

  setupRoundTargetPool() {
    const correctTerm = this.roundsData[this.state.currentRoundIndex].term;
    const allTerms = [...this.roundsData.map(r => r.term), ...this.distractorsData];
    const otherTerms = this.shuffle(allTerms.filter(t => t !== correctTerm));

    // Create pool with correct answer and distractors
    this.state.currentRoundTargetPool = [correctTerm];

    // Add distractors
    const distractorCount = Math.min(5, otherTerms.length);
    for (let i = 0; i < distractorCount; i++) {
      this.state.currentRoundTargetPool.push(otherTerms[i]);
    }

    // Shuffle the pool
    this.state.currentRoundTargetPool = this.shuffle(this.state.currentRoundTargetPool);
  }

  startRound() {
    if (this.state.currentRoundIndex >= this.roundsData.length) {
      this.finishGame();
      return;
    }

    // Reset round state
    this.state.gameStopped = false;

    // Clear area
    this.clearGameArea();

    // Update UI
    this.elements.clue.textContent = this.roundsData[this.state.currentRoundIndex].clue;
    this.elements.clue.classList.add('active');
    this.updateHUD();

    // Setup target pool
    this.setupRoundTargetPool();

    // Start spawning targets
    this.startSpawning();

    // Update buttons
    this.elements.startBtn.style.display = 'none';
    this.elements.stopBtn.style.display = 'inline-block';
    this.elements.resetBtn.style.display = 'none';
  }

  startSpawning() {
    // Initial spawn
    for (let i = 0; i < this.state.maxConcurrentTargets; i++) {
      setTimeout(() => this.spawnTarget(), i * 300);
    }

    // Continuous spawning
    this.state.spawnInterval = setInterval(() => {
      if (!this.state.gameStopped) {
        this.spawnTarget();
      }
    }, 1500);
  }

  endRound(success) {
    this.state.gameStopped = true;

    // Clear spawning
    if (this.state.spawnInterval) {
      clearInterval(this.state.spawnInterval);
      this.state.spawnInterval = null;
    }

    // Increase difficulty
    if (success) {
      this.state.difficulty = Math.min(this.state.difficulty + 0.2, 3);
    }

    // Next round or finish
    setTimeout(() => {
      this.state.currentRoundIndex++;

      if (this.state.currentRoundIndex < this.roundsData.length && this.state.triesLeft > 0) {
        this.startRound();
      } else {
        this.finishGame();
      }
    }, success ? 1000 : 500);
  }

  clearGameArea() {
    // Remove all targets
    this.state.activeTargets.forEach(target => {
      if (target.element && target.element.parentNode) {
        target.element.remove();
      }
    });

    this.state.activeTargets.clear();
    this.state.lanesBusy = [false, false, false, false];
  }

  startGame() {
    this.resetGame();
    this.state.gameStopped = false;
    this.startRound();
  }

  stopGame() {
    this.state.gameStopped = true;

    if (this.state.spawnInterval) {
      clearInterval(this.state.spawnInterval);
      this.state.spawnInterval = null;
    }

    this.clearGameArea();
    this.resetGame();
  }

  resetGame() {
    // Reset state
    this.state.currentRoundIndex = 0;
    this.state.score = 0;
    this.state.totalAttempts = 0;
    this.state.triesLeft = 2;
    this.state.gameStopped = true;
    this.state.difficulty = 1;
    this.state.combo = 0;

    // Clear intervals
    if (this.state.spawnInterval) {
      clearInterval(this.state.spawnInterval);
      this.state.spawnInterval = null;
    }

    // Update UI
    this.updateHUD();
    this.elements.clue.textContent = 'Press "Start Game" to begin your mission!';
    this.elements.clue.classList.remove('active');
    this.elements.message.textContent = '';
    this.elements.message.className = '';

    // Update buttons
    this.elements.startBtn.style.display = 'inline-block';
    this.elements.stopBtn.style.display = 'none';
    this.elements.resetBtn.style.display = 'none';

    // Clear game area
    this.clearGameArea();
  }

  finishGame() {
    this.state.gameStopped = true;

    // Clear any remaining intervals
    if (this.state.spawnInterval) {
      clearInterval(this.state.spawnInterval);
      this.state.spawnInterval = null;
    }

    // Clear game area
    this.clearGameArea();

    // Calculate final stats
    const accuracy = this.state.totalAttempts > 0 
      ? Math.round((this.state.score / this.state.totalAttempts) * 100) 
      : 0;

    // Show results
    if (this.state.score === this.roundsData.length) {
      this.elements.clue.textContent = 'Game Complete!';
      this.elements.message.textContent = `🏆 Perfect Score! ${this.state.score}/${this.roundsData.length} with ${accuracy}% accuracy!`;
      this.elements.message.className = 'success-message';
      this.triggerConfetti();
    } else {
      this.elements.clue.textContent = 'Game Over!';
      this.elements.message.textContent = `You lost! Final Score: ${this.state.score}/${this.roundsData.length} (${accuracy}% accuracy)`;
      this.elements.message.className = '';
    }

    // Update buttons
    this.elements.startBtn.style.display = 'none';
    this.elements.stopBtn.style.display = 'none';
    this.elements.resetBtn.style.display = 'inline-block';
  }

  triggerConfetti() {
    const colors = ['#6366f1', '#ec4899', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6'];
    const confettiCount = 100;

    for (let i = 0; i < confettiCount; i++) {
      setTimeout(() => {
        const confetti = document.createElement('div');
        confetti.className = 'confetti';
        confetti.style.cssText = `
          left: ${Math.random() * 100}%;
          width: ${Math.random() * 10 + 5}px;
          height: ${Math.random() * 10 + 5}px;
          background: ${colors[Math.floor(Math.random() * colors.length)]};
          animation-delay: ${Math.random() * 0.5}s;
          animation-duration: ${Math.random() * 1 + 2}s;
        `;

        document.body.appendChild(confetti);

        setTimeout(() => confetti.remove(), 3000);
      }, i * 20);
    }
  }
}

// Initialize game when DOM is ready
document.addEventListener('DOMContentLoaded', () => {
  const game = new TermTargeterGame();
});

