    --shadow: rgba(0,0,0,0.1);
  }
  .study-optimizer {
    max-width: 700px;
    margin: 2rem auto;
    padding: 1.5rem;
    background: var(--bg);
    font-family: 'Helvetica Neue', Arial, sans-serif;
    color: var(--text);
  }
  .study-optimizer h2 {
    font-size: 1.8rem;
    margin-bottom: 0.5rem;
    border-bottom: 2px solid var(--accent);
    padding-bottom: 0.25rem;
  }
  .study-optimizer p {
    margin-bottom: 1.5rem;
    line-height: 1.6;
  }
  .dp-card {
    border: 1px solid var(--border);
    border-radius: 12px;
    box-shadow: 0 4px 12px var(--shadow);
    margin-bottom: 1rem;
    overflow: hidden;
  }
  .card-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1rem 1.25rem;
    cursor: pointer;
    background: var(--bg);
  }
  .card-header h3 {
    margin: 0;
    font-size: 1.2rem;
  }
  .indicator {
    font-size: 1.2rem;
    font-weight: bold;
    transition: transform 0.3s;
  }
  .dp-card.active .indicator {
    transform: rotate(45deg);
  }
  .card-body {
    max-height: 0;
    overflow: hidden;
    transition: max-height 0.4s ease;
    padding: 0 1.25rem;
    background: var(--bg);
  }
  .dp-card.active .card-body {
    max-height: 500px;
    padding-bottom: 1rem;
  }
  .card-body h4 {
    margin: 1rem 0 0.5rem;
    font-size: 1rem;
    color: var(--accent);
  }
  .card-body ul {
    margin: 0 0 0.5rem 1.25rem;
    padding: 0;
  }
  .card-body li {
    margin-bottom: 0.5rem;
    line-height: 1.4;
  }
</style>

<script>
  document.querySelectorAll('.study-optimizer .card-header')
    .forEach(header => {
      header.addEventListener('click', () => {
        header.parentElement.classList.toggle('active');
      });
    });
</script>
